// PP_FOREACH_WITH_PARAM : apply macro with additional parameter to each item (up to 100 variadic arguments)
#define PP_FOREACH_WITH_PARAM(MACRO,PARAM,LIST)     __PP_FOREACH_WITH_PARAM_IN_RANGE(COUNT_VARIADIC_ARGS(LIST), MACRO, PARAM, LIST)

#define __PP_FOREACH_WITH_PARAM_IN_RANGE(N,M,PARAM,LIST)  FOREACH_WITH_PARAM_##N(N, M, PARAM, LIST)

#define __PP_FOREACH_WITH_PARAM_1(M, PARAM, ITEM)   M(PARAM,ITEM)
#define __PP_FOREACH_WITH_PARAM_2(M, PARAM, LIST)   PP_EXPAND_ARG(M(PARAM,__PP_GET_FIRST_VARIADIC_ARG(LIST))) FOREACH_WITH_PARAM_1(M, PARAM, __PP_SKIP_FIRST_VARIADIC_ARG(LIST))
